const chunkSize = 1000;
const chunkOverlap = 200;
//const text = $node["ABC"].json.data.replace(/\n/, '');
const text = $items("Document Data")[0].json.data.replace(/\n/, '');

const chunks = [];
let remainingText = text;

while (remainingText.length > 0) {
    let splitPoint;

    // Try splitting at paragraph level first
    splitPoint = remainingText.lastIndexOf("\n\n", chunkSize);

    // If no paragraph split, try splitting at sentence level
    if (splitPoint === -1) {
        splitPoint = remainingText.lastIndexOf(". ", chunkSize);
    }

    // If no sentence split, try splitting at word level
    if (splitPoint === -1) {
        splitPoint = remainingText.lastIndexOf(" ", chunkSize);
    }

    // If still no split point, force cut at chunkSize
    if (splitPoint === -1 || splitPoint < chunkSize * 0.5) {  
        splitPoint = chunkSize; // Hard split if no good split point
    }

    // Extract chunk and adjust remaining text with overlap
    let chunk = remainingText.substring(0, splitPoint).trim();
    chunks.push(chunk);

    // Move the pointer forward while keeping the overlap
    remainingText = remainingText.substring(Math.max(0, splitPoint - chunkOverlap)).trim();

    // Break if remaining text is too small to form another chunk
    if (remainingText.length < chunkSize * 0.2) {
        chunks.push(remainingText);
        break;
    }
}

return { chunks };
