const data = $input.item.json;
const binaryData = $input.item.binary;

let output = [];

Object.keys(binaryData)
  .filter(label => label.startsWith("Supporting_Documents"))
  .forEach(label => {
    output.push({
      json: data,
      binary: { data: binaryData[label] }
    });
  });

return output;

const requestForm = $('On form submission').first().json;

return {
      ProjectID: requestForm["Project ID"],
      ProjectTitle: requestForm["Project Title"],
      BusinessDomain: requestForm["Business Domain"],
      Notes: requestForm["Notes"],
      Email: requestForm["Email"],
      Status: 'Submitted',
      SubmittedAt: requestForm["submittedAt"]
    }

const request = $('Merge content').item.json.Request;
return {
  ProjectID:request.ProjectID,
  ProjectTitle:request.ProjectTitle,
  BusinessDomain:request.BusinessDomain,
  Notes:request.Notes,
  Email:request.Email,
  SubmittedAt:request.SubmittedAt,
  Status:'Completed'
};

