// Get the array of items from previous node
const items = $input.all(); 

// Merge "output" fields into one string
const mergedOutput = items
  .map(item => item.json.output) // get each output value
  .filter(Boolean)               // remove null/empty
  .join(' ');                     // join with space (or '\n' for newline)

// Return as single item
return [
  {
    json: {
      mergedOutput,
      Request: $('Request completed?').first().json
    }
  }
];
