const src = JSON.stringify($('AI Agent').first().json.output);
const size = 1800; // safe chunk size for Notion text
const blocks = [];
for (let i = 0; i < src.length; i += size) {
  blocks.push({
    type: 'paragraph',
    paragraph: {
      // language: 'json',
      rich_text: [{ type: 'text', text: { content: src.slice(i, i + size) } }]
    }
  });
}
return [{ children: blocks, title: "AML JSON" }];
