// 1. Pull in the form data
const form = $('Get Client').first().json;

// 2. List the four fields you want to convert
const fields = [
  "Report from start date",
  "Report to this end date",
  "Previous start date",
  "Previous end date"
];

// 3. Loop over each field and remove hyphens
fields.forEach(field => {
  const val = form[field];
  if (val && typeof val === 'string') {
    form[field] = val.replace(/-/g, '');
  }
});

// 4. Return one item containing your reformatted dates
return [
  {
    json: form
  }
];
