:root {
	/* Typography */
	--font-family: 'Inter', 'Segoe UI', 'Roboto', -apple-system, BlinkMacSystemFont, sans-serif;
	--font-weight-light: 300;
	--font-weight-normal: 400;
	--font-weight-medium: 500;
	--font-weight-semibold: 600;
	--font-weight-bold: 700;

	/* Font Sizes */
	--font-size-xs: 12px;
	--font-size-sm: 13px;
	--font-size-base: 14px;
	--font-size-md: 15px;
	--font-size-lg: 16px;
	--font-size-xl: 18px;
	--font-size-2xl: 20px;
	--font-size-3xl: 24px;
	--font-size-4xl: 28px;
	--font-size-5xl: 32px;

	/* Specific Element Font Sizes */
	--font-size-body: var(--font-size-base);
	--font-size-label: var(--font-size-md);
	--font-size-input: var(--font-size-base);
	--font-size-header: var(--font-size-4xl);
	--font-size-subheader: var(--font-size-lg);
	--font-size-paragraph: var(--font-size-base);
	--font-size-link: var(--font-size-sm);
	--font-size-error: var(--font-size-sm);
	--font-size-test-notice: var(--font-size-sm);

	/* HTML Heading Sizes */
	--font-size-html-h1: var(--font-size-5xl);
	--font-size-html-h2: var(--font-size-3xl);
	--font-size-html-h3: var(--font-size-xl);
	--font-size-html-h4: var(--font-size-lg);
	--font-size-html-h5: var(--font-size-base);
	--font-size-html-h6: var(--font-size-sm);

	/* Color Palette - Professional Tech Theme */
	--color-primary: #2563eb;          /* Blue - Primary brand */
	--color-primary-hover: #1d4ed8;    /* Darker blue on hover */
	--color-primary-light: #dbeafe;    /* Light blue background */
	--color-secondary: #059669;        /* Green - Success/Tech */
	--color-accent: #7c3aed;          /* Purple - Automation theme */
	--color-warning: #d97706;         /* Orange - Warning */
	--color-error: #dc2626;           /* Red - Error states */

	/* Neutral Colors */
	--color-background: #fafbfc;
	--color-surface: #ffffff;
	--color-surface-elevated: #ffffff;

	/* Text Colors */
	--color-text-primary: #111827;
	--color-text-secondary: #4b5563;
	--color-text-tertiary: #6b7280;
	--color-text-placeholder: #9ca3af;
	--color-text-disabled: #d1d5db;

	/* Border Colors */
	--color-border-light: #e5e7eb;
	--color-border-medium: #d1d5db;
	--color-border-focus: var(--color-primary);
	--color-border-error: var(--color-error);

	/* Component Specific Colors */
	--color-card-bg: var(--color-surface);
	--color-card-border: var(--color-border-light);
	--color-card-shadow: rgba(59, 130, 246, 0.08);

	--color-header: var(--color-text-primary);
	--color-header-subtext: var(--color-text-secondary);
	--color-label: var(--color-text-secondary);
	--color-input-border: var(--color-border-light);
	--color-input-text: var(--color-text-primary);
	--color-focus-border: var(--color-primary);

	--color-submit-btn-bg: var(--color-primary);
	--color-submit-btn-text: #ffffff;
	--color-submit-btn-hover: var(--color-primary-hover);

	--color-link: var(--color-primary);
	--color-required: var(--color-error);
	--color-clear-button-bg: var(--color-text-tertiary);
	--color-html-text: var(--color-text-secondary);
	--color-html-link: var(--color-primary);

	/* Test Notice Colors */
	--color-test-notice-text: var(--color-warning);
	--color-test-notice-bg: #fef3c7;
	--color-test-notice-border: #fcd34d;

	/* Border Radii - Modern Rounded Design */
	--border-radius-xs: 4px;
	--border-radius-sm: 6px;
	--border-radius-md: 8px;
	--border-radius-lg: 12px;
	--border-radius-xl: 16px;
	--border-radius-2xl: 20px;
	--border-radius-full: 50%;

	/* Component Specific Radii */
	--border-radius-card: var(--border-radius-xl);
	--border-radius-input: var(--border-radius-md);
	--border-radius-clear-btn: var(--border-radius-full);
	--card-border-radius: var(--border-radius-card);

	/* Spacing System - 8px Base Grid */
	--space-1: 4px;
	--space-2: 8px;
	--space-3: 12px;
	--space-4: 16px;
	--space-5: 20px;
	--space-6: 24px;
	--space-8: 32px;
	--space-10: 40px;
	--space-12: 48px;
	--space-16: 64px;
	--space-20: 80px;

	/* Component Specific Spacing */
	--padding-container-top: var(--space-8);
	--padding-card: var(--space-6);
	--padding-test-notice-vertical: var(--space-3);
	--padding-test-notice-horizontal: var(--space-4);
	--margin-bottom-card: var(--space-4);
	--padding-form-input: var(--space-3);
	--card-padding: var(--space-6);
	--card-margin-bottom: var(--space-4);

	/* Dimensions */
	--container-width: 520px;
	--container-max-width: 90vw;
	--submit-btn-height: 52px;
	--checkbox-size: 20px;
	--input-height: 48px;

	/* Shadows - Layered Design */
	--shadow-xs: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
	--shadow-sm: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
	--shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
	--shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
	--shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);

	/* Component Specific Shadows */
	--box-shadow-card: var(--shadow-lg);
	--box-shadow-input-focus: 0 0 0 3px rgba(37, 99, 235, 0.1);
	--box-shadow-button-hover: var(--shadow-md);

	/* Transitions */
	--transition-fast: 150ms cubic-bezier(0.4, 0, 0.2, 1);
	--transition-normal: 250ms cubic-bezier(0.4, 0, 0.2, 1);
	--transition-slow: 350ms cubic-bezier(0.4, 0, 0.2, 1);

	/* Z-Index Scale */
	--z-dropdown: 1000;
	--z-sticky: 1020;
	--z-fixed: 1030;
	--z-modal-backdrop: 1040;
	--z-modal: 1050;
	--z-popover: 1060;
	--z-tooltip: 1070;

	/* Opacity Values */
	--opacity-placeholder: 0.6;
	--opacity-disabled: 0.5;
	--opacity-hover: 0.8;

	/* Breakpoints for Responsive Design */
	--breakpoint-sm: 640px;
	--breakpoint-md: 768px;
	--breakpoint-lg: 1024px;
	--breakpoint-xl: 1280px;
}

/* Enhanced Global Styles */
* {
	box-sizing: border-box;
}

body {
	font-family: var(--font-family);
	background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
	margin: 0;
	padding: 0;
	min-height: 100vh;
	line-height: 1.6;
	color: var(--color-text-primary);
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;
}

/* Container Enhancements */
.form-container {
	max-width: var(--container-width);
	width: var(--container-max-width);
	margin: 0 auto;
	padding: var(--padding-container-top) var(--space-4);
}

/* Card Styling */
.form-card {
	background: var(--color-card-bg);
	border: 1px solid var(--color-card-border);
	border-radius: var(--card-border-radius);
	box-shadow: var(--box-shadow-card);
	padding: var(--card-padding);
	margin-bottom: var(--card-margin-bottom);
	position: relative;
	overflow: hidden;
}

.form-card::before {
	content: '';
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	height: 4px;
	background: linear-gradient(90deg, var(--color-primary), var(--color-secondary), var(--color-accent));
}

/* Header Styling */
.form-header {
	text-align: center;
	margin-bottom: var(--space-8);
}

.form-title {
	font-size: var(--font-size-header);
	font-weight: var(--font-weight-bold);
	color: var(--color-header);
	margin: 0 0 var(--space-3) 0;
	background: linear-gradient(135deg, var(--color-primary), var(--color-accent));
	-webkit-background-clip: text;
	-webkit-text-fill-color: transparent;
	background-clip: text;
}

.form-description {
	font-size: var(--font-size-base);
	color: var(--color-text-secondary);
	margin: 0;
	line-height: 1.7;
	text-align: left;
	background: rgba(249, 250, 251, 0.8);
	padding: var(--space-6);
	border-radius: var(--border-radius-lg);
	border: 1px solid var(--color-border-light);
	backdrop-filter: blur(10px);
	position: relative;
	overflow: hidden;
}

.form-description::before {
	content: '';
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	height: 3px;
	background: linear-gradient(90deg, var(--color-primary) 0%, var(--color-secondary) 50%, var(--color-accent) 100%);
}

.form-description h4 {
	font-size: var(--font-size-lg);
	font-weight: var(--font-weight-semibold);
	color: var(--color-text-primary);
	margin: 0 0 var(--space-3) 0;
	display: flex;
	align-items: center;
	gap: var(--space-2);
}

.form-description h4::before {
	content: attr(data-icon);
	font-size: var(--font-size-xl);
	line-height: 1;
}

.form-description ul {
	list-style: none;
	padding: 0;
	margin: 0 0 var(--space-4) 0;
}

.form-description li {
	margin-bottom: var(--space-2);
	padding-left: var(--space-6);
	position: relative;
	font-size: var(--font-size-sm);
	line-height: 1.6;
}

.form-description li::before {
	content: '▶';
	position: absolute;
	left: 0;
	top: 0;
	color: var(--color-primary);
	font-size: var(--font-size-xs);
	transform: scale(0.8);
}

.form-description strong {
	color: var(--color-text-primary);
	font-weight: var(--font-weight-semibold);
}

.form-description a {
	color: var(--color-primary);
	text-decoration: none;
	font-weight: var(--font-weight-medium);
	border-bottom: 1px solid transparent;
	transition: all var(--transition-fast);
}

.form-description a:hover {
	border-bottom-color: var(--color-primary);
	color: var(--color-primary-hover);
}

.form-description .highlight-box {
	background: linear-gradient(135deg, var(--color-primary-light), rgba(124, 58, 237, 0.1));
	padding: var(--space-4);
	border-radius: var(--border-radius-md);
	margin: var(--space-4) 0;
	border-left: 4px solid var(--color-primary);
}

.form-description .highlight-box strong {
	color: var(--color-primary);
}

/* Course Details Section Styling */
.course-section {
	margin-bottom: var(--space-5);
	padding: var(--space-4);
	background: rgba(255, 255, 255, 0.6);
	border-radius: var(--border-radius-md);
	border: 1px solid rgba(37, 99, 235, 0.1);
}

.course-section:last-child {
	margin-bottom: 0;
}

.section-title {
	display: flex;
	align-items: center;
	gap: var(--space-2);
	font-size: var(--font-size-md);
	font-weight: var(--font-weight-semibold);
	color: var(--color-text-primary);
	margin-bottom: var(--space-3);
	padding-bottom: var(--space-2);
	border-bottom: 2px solid var(--color-primary-light);
}

.section-icon {
	font-size: var(--font-size-lg);
	color: var(--color-primary);
}

/* Payment Structure Styling */
.payment-grid {
	display: grid;
	grid-template-columns: 1fr 1fr;
	gap: var(--space-4);
	margin: var(--space-3) 0;
}

@media (max-width: 640px) {
	.payment-grid {
		grid-template-columns: 1fr;
	}
}

.payment-option {
	background: rgba(5, 150, 105, 0.05);
	padding: var(--space-3);
	border-radius: var(--border-radius-sm);
	border-left: 3px solid var(--color-secondary);
	font-size: var(--font-size-sm);
}

.payment-option.split-payment {
	background: rgba(124, 58, 237, 0.05);
	border-left-color: var(--color-accent);
}

/* Mentorship CTA Styling */
.mentorship-cta {
	background: linear-gradient(135deg, var(--color-accent), var(--color-primary));
	color: white;
	padding: var(--space-4);
	border-radius: var(--border-radius-lg);
	text-align: center;
	margin-top: var(--space-4);
	position: relative;
	overflow: hidden;
}

.mentorship-cta::before {
	content: '';
	position: absolute;
	top: -50%;
	left: -50%;
	width: 200%;
	height: 200%;
	background: linear-gradient(45deg, transparent, rgba(255,255,255,0.1), transparent);
	transform: rotate(45deg);
	animation: shimmer 3s infinite;
}

@keyframes shimmer {
	0% { transform: translateX(-100%) translateY(-100%) rotate(45deg); }
	100% { transform: translateX(100%) translateY(100%) rotate(45deg); }
}

.mentorship-cta strong {
	color: white;
	font-size: var(--font-size-md);
}

.mentorship-cta a {
	color: rgba(255, 255, 255, 0.9);
	font-weight: var(--font-weight-medium);
	border-bottom: 1px solid rgba(255, 255, 255, 0.3);
	transition: all var(--transition-fast);
}

.mentorship-cta a:hover {
	color: white;
	border-bottom-color: white;
}

/* Dark mode adjustments for description */
@media (prefers-color-scheme: dark) {
	.form-description {
		background: rgba(30, 41, 59, 0.8);
		border-color: var(--color-border-light);
	}

	.course-section {
		background: rgba(51, 65, 85, 0.6);
		border-color: rgba(148, 163, 184, 0.1);
	}

	.payment-option {
		background: rgba(5, 150, 105, 0.1);
	}

	.payment-option.split-payment {
		background: rgba(124, 58, 237, 0.1);
	}
}

/* Form Field Styling */
.form-field {
	margin-bottom: var(--space-6);
}

.form-label {
	display: block;
	font-size: var(--font-size-label);
	font-weight: var(--font-weight-medium);
	color: var(--color-label);
	margin-bottom: var(--space-2);
	position: relative;
}

.form-label.required::after {
	content: '*';
	color: var(--color-required);
	margin-left: var(--space-1);
	font-weight: var(--font-weight-bold);
}

/* Input Styling */
.form-input,
.form-select,
.form-textarea {
	width: 100%;
	height: var(--input-height);
	padding: var(--padding-form-input) var(--space-4);
	font-size: var(--font-size-input);
	font-family: var(--font-family);
	color: var(--color-input-text);
	background: var(--color-surface);
	border: 2px solid var(--color-input-border);
	border-radius: var(--border-radius-input);
	transition: all var(--transition-fast);
	outline: none;
}

.form-textarea {
	height: auto;
	min-height: 120px;
	resize: vertical;
	padding-top: var(--space-3);
	padding-bottom: var(--space-3);
}

.form-input:focus,
.form-select:focus,
.form-textarea:focus {
	border-color: var(--color-focus-border);
	box-shadow: var(--box-shadow-input-focus);
	transform: translateY(-1px);
}

.form-input::placeholder,
.form-textarea::placeholder {
	color: var(--color-text-placeholder);
	opacity: var(--opacity-placeholder);
}

.form-input:hover,
.form-select:hover,
.form-textarea:hover {
	border-color: var(--color-border-medium);
}

/* Select Styling */
.form-select {
	background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 20 20'%3e%3cpath stroke='%236b7280' stroke-linecap='round' stroke-linejoin='round' stroke-width='1.5' d='m6 8 4 4 4-4'/%3e%3c/svg%3e");
	background-position: right var(--space-3) center;
	background-repeat: no-repeat;
	background-size: 16px;
	padding-right: var(--space-10);
	cursor: pointer;
}

/* Checkbox and Radio Styling */
.form-checkbox,
.form-radio {
	width: var(--checkbox-size);
	height: var(--checkbox-size);
	margin-right: var(--space-3);
	accent-color: var(--color-primary);
	cursor: pointer;
}

.form-checkbox-label,
.form-radio-label {
	display: flex;
	align-items: flex-start;
	cursor: pointer;
	font-size: var(--font-size-base);
	line-height: 1.5;
	margin-bottom: var(--space-3);
}

/* Multiple Choice Styling */
.form-multiple-choice {
	display: grid;
	gap: var(--space-3);
}

/* Submit Button Styling */
.form-submit {
	width: 100%;
	height: var(--submit-btn-height);
	padding: 0 var(--space-6);
	font-size: var(--font-size-lg);
	font-weight: var(--font-weight-semibold);
	color: var(--color-submit-btn-text);
	background: var(--color-submit-btn-bg);
	border: none;
	border-radius: var(--border-radius-input);
	cursor: pointer;
	transition: all var(--transition-normal);
	position: relative;
	overflow: hidden;
	text-transform: uppercase;
	letter-spacing: 0.5px;
}

.form-submit::before {
	content: '';
	position: absolute;
	top: 0;
	left: -100%;
	width: 100%;
	height: 100%;
	background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
	transition: left var(--transition-slow);
}

.form-submit:hover {
	background: var(--color-submit-btn-hover);
	box-shadow: var(--box-shadow-button-hover);
	transform: translateY(-2px);
}

.form-submit:hover::before {
	left: 100%;
}

.form-submit:active {
	transform: translateY(0);
	box-shadow: var(--shadow-sm);
}

/* Error Styling */
.form-error {
	color: var(--color-error);
	font-size: var(--font-size-error);
	margin-top: var(--space-1);
	display: flex;
	align-items: center;
	gap: var(--space-1);
}

.form-error::before {
	content: '⚠';
	font-size: var(--font-size-sm);
}

.form-input.error,
.form-select.error,
.form-textarea.error {
	border-color: var(--color-border-error);
	box-shadow: 0 0 0 3px rgba(220, 38, 38, 0.1);
}

/* Test Notice Styling */
.test-notice {
	background: var(--color-test-notice-bg);
	color: var(--color-test-notice-text);
	border: 1px solid var(--color-test-notice-border);
	border-radius: var(--border-radius-md);
	padding: var(--padding-test-notice-vertical) var(--padding-test-notice-horizontal);
	margin-bottom: var(--space-6);
	font-size: var(--font-size-test-notice);
	display: flex;
	align-items: center;
	gap: var(--space-2);
}

.test-notice::before {
	content: 'ℹ';
	font-weight: var(--font-weight-bold);
	font-size: var(--font-size-base);
}

/* Progress Indicator */
.form-progress {
	height: 4px;
	background: var(--color-border-light);
	border-radius: var(--border-radius-sm);
	margin-bottom: var(--space-6);
	overflow: hidden;
}

.form-progress-bar {
	height: 100%;
	background: linear-gradient(90deg, var(--color-primary), var(--color-secondary));
	border-radius: var(--border-radius-sm);
	transition: width var(--transition-slow);
	width: 0%;
}

/* Loading State */
.form-loading {
	opacity: var(--opacity-disabled);
	pointer-events: none;
	position: relative;
}

.form-loading::after {
	content: '';
	position: absolute;
	top: 50%;
	left: 50%;
	width: 20px;
	height: 20px;
	margin: -10px 0 0 -10px;
	border: 2px solid var(--color-primary);
	border-top-color: transparent;
	border-radius: var(--border-radius-full);
	animation: spin 1s linear infinite;
}

@keyframes spin {
	to {
		transform: rotate(360deg);
	}
}

/* Responsive Design */
@media (max-width: 640px) {
	:root {
		--container-width: 100%;
		--padding-container-top: var(--space-4);
		--card-padding: var(--space-4);
		--font-size-header: var(--font-size-3xl);
	}

	.form-container {
		padding: var(--space-4) var(--space-3);
	}

	.form-submit {
		height: 48px;
		font-size: var(--font-size-base);
	}
}

/* Dark Mode Support */
@media (prefers-color-scheme: dark) {
	:root {
		--color-background: #0f172a;
		--color-surface: #1e293b;
		--color-surface-elevated: #334155;
		--color-text-primary: #f8fafc;
		--color-text-secondary: #cbd5e1;
		--color-text-tertiary: #94a3b8;
		--color-text-placeholder: #64748b;
		--color-border-light: #334155;
		--color-border-medium: #475569;
		--color-card-border: #334155;
		--color-card-shadow: rgba(0, 0, 0, 0.3);
	}

	body {
		background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
	}
}

/* Accessibility Improvements */
@media (prefers-reduced-motion: reduce) {
	* {
		animation-duration: 0.01ms !important;
		animation-iteration-count: 1 !important;
		transition-duration: 0.01ms !important;
	}
}

.sr-only {
	position: absolute;
	width: 1px;
	height: 1px;
	padding: 0;
	margin: -1px;
	overflow: hidden;
	clip: rect(0, 0, 0, 0);
	white-space: nowrap;
	border: 0;
}

