// Extract page_info from Link header for pagination - FIXED for multiple links
const items = $input.all();
let pageInfo = null;
let linkHeader = null;
let debugInfo = {};

for (const item of items) {
  // Log the structure for debugging
  debugInfo.itemKeys = Object.keys(item.json);

  // Try different possible locations for headers
  if (item.json.headers) {
    linkHeader = item.json.headers.link || item.json.headers.Link || item.json.headers['link'] || item.json.headers['Link'];
    debugInfo.headersFound = true;
    debugInfo.headerKeys = Object.keys(item.json.headers);
  } else if (item.json.response && item.json.response.headers) {
    linkHeader = item.json.response.headers.link || item.json.response.headers.Link;
    debugInfo.responseHeadersFound = true;
  } else if (item.json.header) {
    linkHeader = item.json.header.link || item.json.header.Link;
    debugInfo.headerFound = true;
  }

  if (linkHeader) {
    debugInfo.linkHeaderFound = linkHeader;
    break;
  }
}

if (linkHeader) {
  debugInfo.rawLinkHeader = linkHeader;

  // FIXED: Handle multiple links by splitting on comma first
  const linkParts = linkHeader.split(',').map(part => part.trim());
  debugInfo.linkParts = linkParts;

  // Find the "next" link specifically
  for (const linkPart of linkParts) {
    debugInfo.processingPart = linkPart;

    // Check if this part contains rel="next"
    if (/rel=["']?next["']?/i.test(linkPart)) {
      debugInfo.foundNextLink = linkPart;

      // Extract the URL from this specific "next" link part
      const urlMatch = linkPart.match(/<([^>]+)>/);
      if (urlMatch && urlMatch[1]) {
        try {
          const url = new URL(urlMatch[1]);
          pageInfo = url.searchParams.get('page_info');
          debugInfo.extractedUrl = urlMatch[1];
          debugInfo.extractedPageInfo = pageInfo;
          break;
        } catch (error) {
          debugInfo.urlParseError = error.message;
        }
      }
    }
  }

  // Fallback: if no page_info found, try direct extraction
  if (!pageInfo) {
    // Look for page_info parameter in any "next" link
    for (const linkPart of linkParts) {
      if (/rel=["']?next["']?/i.test(linkPart)) {
        const pageInfoMatch = linkPart.match(/page_info=([^&>\s]+)/i);
        if (pageInfoMatch) {
          pageInfo = pageInfoMatch[1];
          debugInfo.directExtraction = true;
          debugInfo.extractedFrom = linkPart;
          break;
        }
      }
    }
  }
}

// Return the page info with debug information
return [{
  json: {
    page_info: pageInfo,
    page_info_next: pageInfo, // Keep both for compatibility
    has_next_page: pageInfo !== null,
    debug: debugInfo,
    raw_link_header: linkHeader
  }
}];

// Filter products - Must have image AND empty body_html AND currSeas:SS2025 tag (ALL mandatory)
// Enhanced with duplicate prevention + tag extraction for x-styleCode, country_of_origin, and gender
const filteredProducts = [];
const processedProductIds = new Set();

// Helper: extract "key:value" from comma-separated tags string (case-insensitive key)
function getTagValue(tags, key) {
  if (!tags) return '';
  const tagStr = Array.isArray(tags) ? tags.join(',') : String(tags);
  const parts = tagStr.split(',').map(t => t.trim()).filter(Boolean);
  const lowerKey = key.toLowerCase();
  for (const part of parts) {
    const [k, ...rest] = part.split(':');
    if (!k) continue;
    if (k.trim().toLowerCase() === lowerKey) {
      return rest.join(':').trim(); // keep value as-is; supports extra colons in value
    }
  }
  return '';
}

for (const item of $input.all()) {
  // Add null checking for nested properties
  if (!item || !item.json || !item.json.body) {
    continue;
  }

  // Products are inside body property
  const products = item.json.body.products || [];

  if (!Array.isArray(products)) {
    continue;
  }

  for (const product of products) {
    // Add null checking for product object
    if (!product || !product.id) continue;

    // Skip if we've already processed this product ID
    if (processedProductIds.has(product.id)) {
      continue;
    }

    // Mark as processed
    processedProductIds.add(product.id);

    // Check if body_html is empty
    const hasEmptyBodyHtml = !product.body_html || product.body_html.trim() === '';

    // Check if images exist and have valid src
    const hasValidImage = product.images &&
                         Array.isArray(product.images) &&
                         product.images.length > 0 && 
                         product.images[0] && 
                         product.images[0].src && 
                         product.images[0].src.trim() !== '';

    // Check for currSeas:SS2025 tag (case insensitive)
    const tags = product.tags || '';
    const hasCurrentSeasonTag = /currseas:\s*ss2025/i.test(tags);

    // Process ONLY if: has valid image AND empty body_html AND has season tag (ALL mandatory)
    if (hasValidImage && hasEmptyBodyHtml && hasCurrentSeasonTag) {
      // Extract additional fields from tags
      const styleCode = getTagValue(tags, 'x-styleCode');
      const countryOfOrigin = getTagValue(tags, 'country_of_origin');
      const gender = getTagValue(tags, 'gender'); // NEW: gender:Mens, etc.

      filteredProducts.push({
        "Product ID": product.id,
        "Product Title": product.title,
        "Product Type": product.product_type,
        "Vendor": product.vendor,
        "Tags": product.tags,
        "x-styleCode": styleCode,
        "country_of_origin": countryOfOrigin,
        "gender": gender,                         // NEW column
        "Image URL": product.images[0].src,
        "Current Description": product.body_html || '',
        "Has Valid Image": hasValidImage,
        "Has Empty Body": hasEmptyBodyHtml,
        "Has Season Tag": hasCurrentSeasonTag,
        "Filter Reason": "Has valid image + Empty body_html + Has currSeas:SS2025 tag",
        "Status": "Ready for AI Description",
        "Processing Timestamp": new Date().toISOString()
      });
    }
  }
}

// Return ONLY filtered products (empty array if none match)
return filteredProducts;
